/* Reload to Regenerate */
t=0
draw=_=>{t||createCanvas(W=600,W)
background(0)
w=W/2
noFill(stroke`#つぶやきProcessing`)
s=.05
for(x=-1;x<1;x+=s)for(y=-1;y<1;y+=s){quad(...[[x,y],[x+s,y],[x+s,y+s],[x,y+s]].flatMap(([x,y,d=mag(x,y^2),s=t*PI/2,c=noise(cos(d+s),sin(d+s))])=>[c*x*W+w,c*y*W+w]))}t+=.01}
