// The moment between sleep and waking up

let seed;

function setup() {  
  createCanvas(400, 400);

  randomSeed(4072.8341998288233);
  blendMode(DARKEST);
}

function draw() {
  pixelDensity(30);

  background(rcb());

  fill(rc());
  noStroke();

  let delta = 10;
  let size = 250;

  circle(200,200,size);
  for (var i = 0; i < 20; i++) {
    fill(rc());
    circle(200+delta*int(random(-3,3)), 200+delta*int(random(-3,3)), size);
  }

  noLoop();
}

function rcb() {
  let c = random(200,255);
  return color(c, c, c);
}

function rc() {
  return color(random(255), random(255), random(255), random(200));
}
